import { useContext, useState } from 'react'
import axios from 'axios'

import { Context } from "../../context/Context";
import'./write.css'

export default function Write() {

  const [title, setTitle] = useState("")
  const [desc, setDesc] = useState("")
  const [file, setFile] = useState(null)
  const [categories, setCategories] = useState([])
  const {user} = useContext(Context)
 

  const handleSubmit = async(e) => {
    e.preventDefault()

    const newPost = {
      username: user.username,
      title,
      desc
    }
    console.log(newPost)
    if(file){
      const data = new FormData()
      const filename = Date.now() + file.name;
      data.append("name",filename)
      data.append("file",file)
      newPost.photo = filename
      try{
        await axios.post('http://localhost:3000/api/upload', data)
      }
      catch(err){

      }
    }
    try {
      const res = await axios.post('http://localhost:3000/api/posts/add', newPost)
      window.location.replace("/post/"+res.data._id)
    }
    catch(err) {}
    
  }

  return (
    <div className="write">
      {file && (
        <img src={URL.createObjectURL(file)} alt="" className="writeImg" />
        )}
        <form className="writeForm" onSubmit={handleSubmit}>
            <div className="writeFormGroup">
                <label htmlFor="fileInput">
                 <i className="writeIcon fa-solid fa-plus"></i>
                </label>
                <input type="file" id="fileInput" style={{display:"none"}} onChange={e => setFile(e.target.files[0])} />
                <input type="text" id="Title" placeholder='Title' className='writeInput' autoFocus={true} onChange={e => setTitle(e.target.value)}/>
            </div>
            <div className="writeFormGroup">
                <textarea 
                placeholder="Tell your story" 
                type="text" 
                className='writeInput writeText'
                onChange={e => setDesc(e.target.value)}
                />
            </div>
            <button className="writeSubmit" type='submit'>Publish</button>
        </form>
    </div>
  )
}
