import { useContext } from 'react'
import axios from 'axios'

import Sidebar from '../../components/sidebar/Sidebar'
import './settings.css'
import { Context } from "../../context/Context";
import { useState } from 'react'

export default function Settings() {
  const [file, setFile] = useState(null)
  const [username, setUsername] = useState(null)
  const [email, setEmail] = useState(null)
  const [password, setPassword] = useState(null)
  const {user} = useContext(Context)

  const handleSubmit = async(e) => {
    e.preventDefault()

    const updatedUser = {
      userId: user._id,
      username,
      email,
      password
    }
    if(file){
      const data = new FormData()
      const filename = Date.now() + file.name;
      data.append("name",filename)
      data.append("file",file)
      updatedUser.profilPic = filename
      try{
        await axios.post('http://localhost:3000/api/upload', data)
      }
      catch(err){
        console.log(err)
      }
    }
    try {
      const res = await axios.post('http://localhost:3000/api/auth/updateAcc', updatedUser)
      window.location.reload()
    }
    catch(err) {
      console.log(err)
    }
     
  }



  return (
    <div className="settings">
        <div className="settingsWrapper">
            <div className="settingsTitle">
                <span className="settingsUpdateTitle">Update Your Account</span>
                <span className="settingsDeleteTitle">Delete Account</span>
            </div>
            <form className="settingsForm">
                <label>Profil Picture</label>
                <div className="settingsPP">
                  <img className='topImg'
                  src={user.profilPic}
                  alt='profil'
                  onChange={e => setFile(e.target.files[0])}>
                  </img>
                  <label htmlFor="fileInput">
                      <i className=" settingsPPIcon fa-solid fa-user"></i>
                  </label>
                  <input type="file" id="fileInput" style={{display:"none"}} />
                </div>
                <label>Username</label>
                <input type="text" placeholder='Celeste' />
                <label>Email</label>
                <input type="text" placeholder='Celeste.Doll@gmail.com' />
                <label>Password</label>
                <input type="password" />
                <button className="settingsSubmit">Update</button>
            </form>
        </div>
        <Sidebar />
    </div>
  )
}
