import Post from '../post/Post'
import './posts.css'

export default function Posts({posts, cats}) {
  // Filtre les elements brouillons
  console.log(posts)
  return (
    <div className="posts">
      {cats ? posts.filter(e=> e.categories.includes(cats)).map(e=>(
        <Post data={e}/>
      ))
        : posts.map(e=> (
          <Post data={e} />
        ))
    }
    </div>
  )
}
