  const [desc, setDesc] = useState()
  const [photo, setPhoto] = useState()
  const [ categories, setCategories] = useState([])

  const handleSubmit = (e) => {
    e.preventdefault;
    axios
  }

  return (
    <div className="write">
        <img src="https://steamuserimages-a.akamaihd.net/ugc/1297549135823923012/B67392C2C04E18F3416D0A23A9E58F29558C1923/?imw=5000&imh=5000&ima=fit&impolicy=Letterbox&imcolor=%23000000&letterbox=false" alt="" className="writeImg" />
