import './singlepost.css'
import { useParams } from 'react-router-dom';
import { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

export default function SinglePost() {


  let { id } = useParams()
  const [post, setPost] = useState()
  const [ modify, setModify] = useState(false)

  const PF ='http://localhost:3000/public/images/'


  useEffect(()=>{
    console.log("hi")
    const getPost = async () =>{
      const res = await axios.get(`http://localhost:3000/api/posts/`+id)
      setPost(res.data)
    }
    getPost()
  },[id])

  const handleChange = () => {
    
  }
  const handleDelete = async() => {
    try{
      console.log(post)
      const res = await axios.delete(`http://localhost:3000/api/posts/${id}`, { data: { username: post.username } })
      if(res){
        alert("Your post has been deleted")
        window.location = "/"
      }
    }
    catch(err){

    }

  }

  console.log(post)
  return (
    <div className="singlePost">
      {post && ( 
        <div className="singlePostWrapper">
          {post.photo && (
              <img src={PF + post.photo}
              alt="Blog Post" 
              className="postImg" />
              )}
          <h1 className="singlePostTitle">{post.title}
          <div className="singlePostEdit">
              <i className="singlePostIcon fa-solid fa-pen-to-square" onClick={handleChange}></i>
              <i className="singlePostIcon fa-solid fa-trash-can" onClick={handleDelete}></i>
          </div>
          </h1>
          <div className="singlePostInfo">
              <span className="singlePostAuthor">Author:
                <Link to={`/?user=${post.username}`} className="link">
                <b> {post.username} </b>
                </Link>
               </span>
              <span className="singlePostDate">{new Date(post.createdAt).toDateString()}</span>
          </div>
          <p className="singlePostDesc">{post.age}</p>
          <p className="singlePostDesc">{post.desc}</p>
        </div>
      )}
    </div>
  )
}
