import './register.css'
import { useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

export default function Register() {
  const [username, setUsername] = useState("")
  const [email, setEmail] = useState("")
  const [password, setPassword] = useState("")
  const [password2, setPassword2] = useState("")
  const [error, setError] = useState(false)
  const [pending, setPending] = useState(false)

  // TO DO - handle Pending (add loading component)
  const handleSubmit = async (e)=>{
    setPending(true)
    e.preventDefault();
    try{
      setError(false)
      const res = await axios.post("http://localhost:3000/api/auth/register", {
        username,
        email,
        password,
        password2
      })
      setPending(false)
      console.log(res)
      res.data && window.location.replace("/login")
    }
    catch(err){
      setPending(false)
      setError(true)
    }
   
  }


  return (
    <div className="register">
        <form className="registerForm" onSubmit={handleSubmit}>
            <label>Username</label>
            <div>
              <input 
                type="text"
                className='registerInput'
                placeholder="Enter your username..." 
                onChange={e=>setUsername(e.target.value)}
                />
            </div>
            <label>Email</label>
            <div>
              <input 
              type="text" 
              className='registerInput' 
              placeholder="Enter your email..." 
              onChange={e=>setEmail(e.target.value)}
              />
            </div>
            
            <label>Password</label>
            <div>
              <input type={pass} 
              className='registerInput' 
              placeholder="Enter your password..." 
              onChange={e=>setPassword(e.target.value)}
              />
              <i className={eye} onClick={handleClick}></i>
            </div>
          
            <label>Confirm Password</label>
            <div>
              <input type={pass}
              className='registerInput' 
              placeholder="Confirm your password" 
              onChange={e=>setPassword2(e.target.value)}
              />
              <i className={eye} onClick={handleClick}></i>
            </div>
            <button className="registerButton">Register</button>
        </form>
        {pending && 
        (<span>Loading...</span>)}
        {error && 
        (<span style={{color:"red", marginTop:"10px"}}>Something went Wrong</span>)}

        <span><Link to="/login" className='linkLogin'>I already have an account !</Link></span>
    </div>
  )
}
