import { useState, useEffect } from 'react';
import Header from '../../components/header/Header'
import Posts from '../../components/posts/Posts'
import Sidebar from '../../components/sidebar/Sidebar'
import './home.css'
import axios from 'axios'
import { useSearchParams } from 'react-router-dom';



export default function Home() {
  const [posts, setPosts] = useState([])
  const [searchParams] = useSearchParams();
  const [cat, setCat] = useState()
 
  useEffect(()=>{
    const fetchPosts = async () => {

      // Only get published posts
      const data = {
        filtered : true,
        categories : searchParams.get('cat'),
      }
      const res = await axios.get(`http://localhost:3000/api/posts/`, data)
      //TODO tout filtrer dans le back
      // const filterPost = res.data.filter(e=> e.isPublished === true)
      // const categories = searchParams.get('cat')
      console.log(res.data)
      setPosts(res.data)
      // setCat(categories);
    }
    fetchPosts()
  },[searchParams])

  // console.log(posts)
  return (
    <>
     <Header />
     <div className="home">
       <Posts posts={posts}/>
       <Sidebar />
    </div>
    </>
   
  )
}
