import './singlepost.css'
import { useParams } from 'react-router-dom';
import { useContext, useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { Context } from '../../context/Context';

export default function SinglePost() {


  let { id } = useParams()
  const [post, setPost] = useState()
  const [ modify, setModify] = useState(false)
  const [title, setTitle] = useState("")
  const [desc, setDesc] = useState("")
  const [file, setFile] = useState(null)
  const [categories, setCategories] = useState([])
  const {user} = useContext(Context)

  const PF ='http://localhost:3000/public/images/'


  useEffect(()=>{
    console.log("hi")
    const getPost = async () =>{
      const res = await axios.get(`http://localhost:3000/api/posts/${id}`)
      setPost(res.data)
    }
    getPost()
  },[id])

  const handleChange = () => {
    setModify(true)
  }
  const confirmChange = async() => {
    setModify(false)
    // try {
      
    //   const res = await axios.put(`http://localhost:3000/api/posts/${id}`, 
    //   {
        
    //   })
    // }
    // catch(err) {
    //   console.log(err)
    // }
  }
  const handleDelete = async() => {
    try{
      console.log(post)
      const res = await axios.delete(`http://localhost:3000/api/posts/${id}`, { data: { username: post.username } })
      if(res){
        alert("Your post has been deleted")
        window.location = "/"
      }
    }
    catch(err){

    }

  }

  console.log(post)
  return (
    <div className="singlePost">
      {post && ( 
        <div className="singlePostWrapper">
          {post.photo && (
              <img src={PF + post.photo}
              alt="Blog Post" 
              className="postImg" />
              )}


          {!modify ? (
             <h1 className="singlePostTitle">{post.title}
                <div className="singlePostEdit">
                  <i className="singlePostIcon fa-solid fa-pen-to-square" onClick={handleChange}></i>
                  <i className="singlePostIcon fa-solid fa-trash-can" onClick={handleDelete}></i>
                </div>
               </h1>
          ) : (
            <input
              type="text"
              className="singlePostTitleInput" 
              onChange={e => setTitle(e.target.value)} 
              defaultValue={`${post.title}`}
              autoFocus>
            </input> 
          )
          }
          <div className="singlePostInfo">
              <span className="singlePostAuthor">Author:
                <Link to={`/?user=${post.username}`} className="link">
                <b> {post.username} </b>
                </Link>
               </span>
              <span className="singlePostDate">{new Date(post.createdAt).toDateString()}</span>
          </div>
          <p className="singlePostDesc">{post.age}</p>
          {!modify ? (
            <p className="singlePostDesc">{post.desc}</p>
          ) : (
            <>
            <input type="text" className="singlePostDescInput" defaultValue={`${post.desc}`} onChange={e => setDesc(e.target.value)}></input>
            <button onClick={confirmChange} className="ConfirmBtn">Confirm Changes<i class="fa-solid fa-check" style={{paddingLeft:'2em'}}></i></button>
            </>
          )}
          
        </div>
      )}
    </div>
  )
}
