import { useContext, useRef } from 'react'
import { Context } from '../../context/context'
import './login.css'
import axios from 'axios'

export default function Login() {

  const userRef = useRef()
  const passwordRef = useRef()
  const {user, dispatchEvent, isFetching} = useContext(Context)

  const handleSubmit = (e) =>{
    e.preventDefault()
    dispatchEvent({type:"LOGIN_START"})
    try{
      const res = axios.post("http://localhost:3000/auth/login", 
      {
        email: userRef.current.value,
        password: passwordRef.current.value,
      })
      dispatchEvent({type:"LOGIN_SUCCESS", payload:res.data})
    }
    catch(err){
    dispatchEvent({type:"LOGIN_FAILURE"})
    }
  }

  console.log(user)
  return (
    <div className="login">
        <span className="loginTitle">Login</span>
        <form className="loginForm" onSubmit={handleSubmit}>
            <label>Username</label>
            <input 
            type="text" 
            className='loginInput' 
            placeholder="Enter your email..." 
            ref={userRef} />
            <label>Password</label>
            <input 
            type="password" 
            className='loginInput' 
            placeholder="Enter your password..."
            ref={passwordRef}
             />
            <button className="loginButton" type="submit">Login</button>
        </form>
        <button className="loginRegisterButton">Register</button>
    </div>
  )
}
